import React, { useState } from 'react';
import YouTube from 'react-youtube';
import Navbar from '../NavBar';
import '../css/MusicSection.css';

const MusicSection = () => {
  const songs = [
    {
      title: 'All Yours',
      artist: 'APRE',
      description: 'Our song. Do I need to say anything else?',
      youtubeUrl: 'https://www.youtube.com/watch?v=OeQz3hpgXkk'
    },
    {
      title: 'Blame Brett',
      artist: 'The Beaches',
      description: 'Loud one!',
      youtubeUrl: 'https://www.youtube.com/watch?v=h2kUX_Fmj7k'
    },
    {
        title: 'Can I Call You Tonight?',
        artist: 'Dayglow',
        description: 'Played in IKEA once, was a crazy experience',
        youtubeUrl: 'https://www.youtube.com/watch?v=hh1WeQxfCX0'
      },
      {
        title: 'Salt',
        artist: 'Bad Suns',
        description: 'Another one we discovered together.',
        youtubeUrl: 'https://www.youtube.com/watch?v=uoBN56799FA'
      },
      {
        title: 'Happiness in Liquid Form',
        artist: 'Alfie Templeman',
        description: 'I know you secretly like this song.',
        youtubeUrl: 'https://www.youtube.com/watch?v=7h4_cD6A5Bs'
      },
      {
        title: 'Alt J - Interlude II (cover)',
        artist: 'Me!',
        description: 'Had to put this in',
        youtubeUrl: 'https://www.youtube.com/watch?v=JLWPxZJ5PX0'
      },

      {
        title: 'Young Dumb & Broke',
        artist: 'Khalid',
        description: 'Best concert of my life, because it was with you',
        youtubeUrl: 'https://www.youtube.com/watch?v=IPfJnp1guPc'
      },



      {
        title: 'Take My Breath Away',
        artist: 'The Weeknd',
        description: 'You definitely take my breath away',
        youtubeUrl: 'https://www.youtube.com/watch?v=eT1E3gmST9U'
      },

      {
        title: 'Out of Time',
        artist: 'The Weeknd',
        description: 'The weird noises you make in the beginning',
        youtubeUrl: 'https://www.youtube.com/watch?v=kxgj5af8zg4'
      },

      {
        title: 'Silence',
        artist: 'Marshmello Ft. Khalid',
        description: 'FIGHTAAA',
        youtubeUrl: 'https://www.youtube.com/watch?v=Tx1sqYc3qas'
      },
      {
        title: 'Steal My Girl',
        artist: 'One Direction',
        description: 'bring back 1D ):',
        youtubeUrl: 'https://www.youtube.com/watch?v=UpsKGvPjAgw'
      },

      {
        title: 'Something About Us',
        artist: 'Daft Punk',
        description: 'bop',
        youtubeUrl: 'https://www.youtube.com/watch?v=em0MknB6wFo'
      },
      {
        title: 'we fell in love in october',
        artist: 'girl in red',
        description: 'we sure did',
        youtubeUrl: 'https://www.youtube.com/watch?v=iggmiF7DNoM'
      },

    // Add more songs as needed
  ];

  const [currentSongIndex, setCurrentSongIndex] = useState(0);

  const getVideoIdFromUrl = (url) => {
    const videoIdMatch = url.match(/(?:\?v=|&v=|youtu\.be\/|\/embed\/|\/v\/|\/\d{1,2}\/|\/vi\/)([^#\&\?]{11})/);
    if (videoIdMatch) {
      return videoIdMatch[1];
    }
    return null;
  };

  const handleNextSong = () => {
    // setCurrentSongIndex((prevIndex) => (prevIndex + 1) % songs.length);
    const randomIndex = Math.floor(Math.random() * songs.length);
    // const randomSong = songs[randomIndex];
    setCurrentSongIndex(randomIndex);
  };

  const currentSong = songs[currentSongIndex];
  const videoId = currentSong?.youtubeUrl ? getVideoIdFromUrl(currentSong.youtubeUrl) : null;

  return (
    <section id="music-section">
      <h2>Music Section</h2>
      <div className="song">
        <h3>{currentSong.title}</h3>
        <p>Artist: {currentSong.artist}</p>
        <p>Description: {currentSong.description}</p>
        {videoId && <YouTube videoId={videoId} />}
      </div>
      <button onClick={handleNextSong}>Shuffle!</button>
    </section>
  );
};

export default MusicSection;
